mapper.select:
SELECT * 
FROM (
   SELECT t.*, ROWNUM AS row_NUM
   FROM (
       SELECT * FROM REVIEWTABLE
       WHERE R_PRODUCTNO = #{no}
   ) t
)
WHERE row_num >= #{current} AND row_num < #{current} + 1

SELECT COALESCE(ROUND(AVG(starscore),1), 0) AS average_value
FROM reviewtable
WHERE R_PRODUCTNO = #{no}
